// NotepadList.tsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import NotepadItem from "./NotepadItem";

interface Notepad {
  _id: string;
  title: string;
  subtitle: string;
  content: string;
}

const NotepadList: React.FC = () => {
  const [notepads, setNotepads] = useState<Notepad[]>([]);

  useEffect(() => {
    const fetchNotepads = async () => {
      const response = await axios.get<Notepad[]>("http://localhost:3001/notepads");
      setNotepads(response.data);
    };

    fetchNotepads();
  }, []);

  const handleDeleteNotepad = async (id: string) => {
    // Implement the logic to delete the notepad on the server
    await axios.delete(`http://localhost:3001/notepads/${id}`);
    setNotepads((prevNotepads) => prevNotepads.filter((notepad) => notepad._id !== id));
  };

  return (
    <ul>
      {notepads.map((notepad: Notepad) => (
        <NotepadItem key={notepad._id} notepad={notepad} onDelete={handleDeleteNotepad} />
      ))}
    </ul>
  );
};

export default NotepadList;
