import React, { useState, useEffect, ChangeEvent } from "react";
import axios from "axios";

interface Notepad {
  _id: string;
  title: string;
  subtitle: string;
  content: string;
}

const App: React.FC = () => {
  const [notepads, setNotepads] = useState<Notepad[]>([]);
  const [newNotepad, setNewNotepad] = useState({ title: "", subtitle: "", content: "" });
  const [isEditing, setIsEditing] = useState(false);
  const [editNoteId, setEditNoteId] = useState("");

  useEffect(() => {
    const fetchNotepads = async () => {
      const response = await axios.get<Notepad[]>("http://localhost:3001/notepads");
      setNotepads(response.data);
    };

    fetchNotepads();
  }, []);

  const handleCreateNotepad = async () => {
    await axios.post("http://localhost:3001/notepads", newNotepad);
    setNewNotepad({ title: "", subtitle: "", content: "" });
    fetchNotepads(); // Atualize a lista após a criação
  };

  const handleEditNotepad = (noteId: string) => {
    setIsEditing(true);
    setEditNoteId(noteId);
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
    setEditNoteId("");
    setNewNotepad({ title: "", subtitle: "", content: "" });
  };

  const handleUpdateNotepad = async () => {
    await axios.put(`http://localhost:3001/notepads/${editNoteId}`, newNotepad);
    const updatedNotepads = notepads.map((note) => (note._id === editNoteId ? { ...note, ...newNotepad } : note));
    setNotepads(updatedNotepads);
    setIsEditing(false);
    setEditNoteId("");
    setNewNotepad({ title: "", subtitle: "", content: "" });
  };

  const handleDeleteNotepad = async (noteId: string) => {
    await axios.delete(`http://localhost:3001/notepads/${noteId}`);
    const updatedNotepads = notepads.filter((note) => note._id !== noteId);
    setNotepads(updatedNotepads);
  };

  const handleInputChange = (e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setNewNotepad((prevNotepad: any) => ({ ...prevNotepad, [name]: value }));
  };

  return (
    <div>
      <h1>Notepads</h1>
      <ul>
        {notepads.map((notepad: { _id: any; title: any; subtitle: any; content: any }) => (
          <li key={notepad._id}>
            <div>{notepad.title}</div>
            <div>{notepad.subtitle}</div>
            <div>{notepad.content}</div>
            <button onClick={() => handleEditNotepad(notepad._id)}>Edit</button>
            <button onClick={() => handleDeleteNotepad(notepad._id)}>Delete</button>
          </li>
        ))}
      </ul>
      <h2>{isEditing ? "Edit Notepad" : "Create New Notepad"}</h2>
      <input type="text" placeholder="Title" name="title" value={newNotepad.title} onChange={handleInputChange} />
      <input
        type="text"
        placeholder="Subtitle"
        name="subtitle"
        value={newNotepad.subtitle}
        onChange={handleInputChange}
      />
      <textarea placeholder="Content" name="content" value={newNotepad.content} onChange={handleInputChange} />
      <button onClick={isEditing ? handleUpdateNotepad : handleCreateNotepad}>
        {isEditing ? "Update Notepad" : "Create Notepad"}
      </button>
      {isEditing && <button onClick={handleCancelEdit}>Cancel Edit</button>}
    </div>
  );
};

export default App;
